    // Build pages dynamically
    imageUrls.forEach((url, index) => {
      const page = document.createElement("div");
      page.className = "page";
      page.style.zIndex = imageUrls.length - index;

      const img = document.createElement("img");
      img.src = url;
      img.alt = "Comic Page " + (index + 1);
