// Get all items output by the HTTP Request1 node
const uploadedImages = $items("HTTP Request1");

// Initialize array to store URLs
const imageUrls = [];

// Loop through each uploaded image
for (const item of uploadedImages) {
    // Each item.json.image.url contains the direct image link
    if(item.json.image && item.json.image.url) {
        imageUrls.push(item.json.image.url);
    }
}

// Return a single JSON object with all URLs
return [
    {
        json: {
            imageUrls
        }
    }
];

// Get all incoming RSS items
const items = $input.all();

// Limit to top 5 only
const topItems = items.slice(0, 10);

// Map them to a cleaner format
return topItems.map((item, i) => {
  const news = item.json;
  return {
    json: {
      index: i + 1,
      title: news.title,
      link: news.link,
      date: news.pubDate,
      description: news.contentSnippet || news.content || "",
      image: news.enclosure?.url || null,
      formatted: `üì∞ ${i + 1}. ${news.title}\n${news.link}`
    }
  };
});

// 1Ô∏è‚É£ Get all articles from the first sheet
let articles = $items("Get row(s) in sheet").map(item => item.json);

// 2Ô∏è‚É£ Get the links field (all links in one cell) from the second sheet
let linksRaw = $items("Get row(s) in sheet1")[0].json.links || "[]";

// 3Ô∏è‚É£ Parse the links string into an array
let links;
try {
  links = JSON.parse(linksRaw);
} catch(e) {
  links = [];
}

// 4Ô∏è‚É£ Assign each article its corresponding image URL
articles.forEach((article, i) => {
  article.link = links[i] || null;
});

// 5Ô∏è‚É£ Return as a single item
return [
  {
    json: {
      articles: articles
    }
  }
];

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
