const panels = $json.data; // your panels array
const storyTitle = panels[0]?.title || "Untitled Story"; // dynamic title from first panel

const html = `<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>${storyTitle} | Panel Verse</title>
  <style>
    /* Base Styles */
    body {
      font-family: 'Arial', sans-serif;
      background-color: #121212 !important;
      color: #eeeeee !important;
      margin: 0;
      padding: 20px;
    }
    header {
      text-align: center;
      margin-bottom: 30px;
      padding: 0 10px;
    }
    header h1 {
      margin: 0;
      font-size: 2.5em;
      color: #ff5bd1 !important;
    }
    header p {
      margin: 5px 0;
      font-style: italic;
      color: #7afcff !important;
      font-size: 1em;
    }
    .panel {
      position: relative;
      background: #1e1e1e !important;
      border-radius: 12px;
      box-shadow: 0 4px 15px rgba(0,0,0,0.5);
      max-width: 600px;
      width: 100%;
      margin: 0 auto 40px auto;
      padding: 20px;
      overflow: hidden;
      box-sizing: border-box;
      color: #eeeeee !important;
    }
    .panel img {
      width: 100%;
      height: auto;
      border-radius: 12px;
      margin-bottom: 15px;
      display: block;
    }
    .watermark {
      position: absolute;
      bottom: 10px;
      right: 10px;
      font-size: 0.9em;
      color: rgba(255,255,255,0.6) !important;
      background: rgba(0,0,0,0.5) !important;
      padding: 2px 6px;
      border-radius: 4px;
      pointer-events: none;
    }
    .panel p {
      margin: 5px 0;
      line-height: 1.5;
      word-wrap: break-word;
      color: #eeeeee !important;
    }
    .panel em {
      color: #bbbbbb !important;
    }
    .panel strong {
      color: #ffffff !important;
    }
    footer {
      text-align: center;
      margin-top: 50px;
      font-size: 0.9em;
      color: #bbbbbb !important;
      line-height: 1.5;
      padding: 0 10px;
    }
    footer a {
      color: #7afcff !important;
      text-decoration: none;
    }

    /* Responsive Media Queries */
    @media (max-width: 768px) {
      header h1 {
        font-size: 2em;
      }
      header p {
        font-size: 0.95em;
      }
      .panel {
        padding: 15px;
        margin-bottom: 30px;
      }
    }

    @media (max-width: 480px) {
      header h1 {
        font-size: 1.6em;
      }
      header p {
        font-size: 0.9em;
      }
      .panel {
        padding: 12px;
        margin-bottom: 25px;
      }
      .watermark {
        font-size: 0.8em;
        padding: 1px 4px;
      }
    }
  </style>
</head>
<body>

  <header>
    <h1>Panel Verse</h1>
    <p>Come And Enjoy Stories Prepared Fresh With Human Effort And AI</p>
  </header>

  ${panels.map(p => `
    <div class="panel">
      <img src="${p.image.url}" alt="Panel ${p.panel}">
      <div class="watermark">Panel Verse</div>
      ${p.narration ? `<p><em>${p.narration}</em></p>` : ""}
      ${p.dialogues.map(d => `<p><strong>${d.speaker}:</strong> ${d.text}</p>`).join("")}
    </div>
  `).join("")}

  <footer>
    <p>Created by Rounak Dutta (running solo)</p>
    <p>For collaborations, company versions, or automations: <a href="mailto:rounak153d@gmail.com">rounak153d@gmail.com</a> | +91-9815121578</p>
  </footer>

</body>
</html>`;

// Return as JSON for n8n
return [{
  json: {
    title: `Panel Verse | ${storyTitle}`,
    html
  }
}];
